/**
 * Execute arbitrary JavaScript code and capture its console output
 * @param {string} codeString - JavaScript code to execute
 * @returns {string} The captured console output
 */
function executeCode(codeString) {
  // Save the original console.log function
  const originalLog = console.log;

  // Create an array to store the output
  const outputLines = [];

  // Override console.log to capture output
  console.log = function(...args) {
    // Convert all arguments to strings and join them
    const output = args.map(arg => 
      typeof arg === 'object' ? JSON.stringify(arg) : String(arg)
    ).join(' ');

    // Add to our captured output
    outputLines.push(output);
  };

  // Variable to store the result
  let result;

  try {
    // Execute the code
    result = eval(codeString);
  } catch (error) {
    // Restore the original console.log
    console.log = originalLog;
    return `Error executing code: ${error.message}`;
  } finally {
    // Restore the original console.log
    console.log = originalLog;
  }

  // Join all captured output lines
  const output = outputLines.join('\n');

  // If there's a result but no console output, return the result
  if (output === '' && result !== undefined) {
    return String(result);
  }

  return output;
}

return executeCode(query.code_to_execute);
